import './App.css';
import { CATEGORIES } from './data'
import ToDoList from './ToDoList'
import Form from './Form'
class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  handleFormSubmit = (e) => {
    e.preventDefault() 
    console.log(e.target)
  } 
  handleDelete = (task) => {
    console.log("DELETE")
  }
  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <Form handleFormSubmit={this.handleFormSubmit}/>
        <ToDoList handleDelete={this.handleDelete} tasks={this.state.tasks} />
      </div>
    );
  }
}


export default App;
