import React from 'react'

class ToDo extends React.Component {
    state = {
        text: "",
        category: ""
    }
    render() {
        return (
            <div className="task">
                <div className="label">{props.task.category}</div>
                <div className="text">{props.task.text}</div>
                <button onClick={props.handleDelete} className="delete">X</button>
            </div>
        )
    }
}

export default ToDo
